import { useState } from "react";
import Navbar from "./components/Navbar";
import ConnectWallet from "./components/ConnectWallet";
// import ContractActions from "./components/ContractActions";
// import BalanceDisplay from "./components/BalanceDisplay";
import GroupSelector from "./components/GroupSelector";
import CreateGroup from "./views/CreateGroup";
import AddExpense from "./views/AddExpense";
import GroupSummary from "./views/GroupSummary";


function App() {
    const [signer, setSigner] = useState(null);
    const [view, setView] = useState("home");
    const [selectedGroupId, setSelectedGroupId] = useState(null);

    return (
      <div className="container">
        <Navbar setView={setView} setSigner={setSigner} />

        {signer && view === "create" && <CreateGroup setGroupId={setSelectedGroupId} />}
        {signer && view === "add" && <AddExpense groupId={selectedGroupId} />}
        {signer && view === "home" && selectedGroupId && <GroupSummary groupId={selectedGroupId} />}

        {!selectedGroupId && view === "home" && (
          <p className="text-center mt-5">No group selected. Create a group to get started.</p>
        )}
      </div>
    );
  }

export default App;
