import { useState, useEffect } from "react";
import { BrowserProvider, formatEther } from "ethers";

export default function Navbar({ setView, setSigner }) {
  const [account, setAccount] = useState(null);
  const [balance, setBalance] = useState("0");

  const connect = async () => {
    if (window.ethereum) {
      const provider = new BrowserProvider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = await provider.getSigner();
      setSigner(signer);

      const addr = await signer.getAddress();
      setAccount(addr);

      const bal = await provider.getBalance(addr);
      setBalance(formatEther(bal));
    } else {
      alert("Please install MetaMask");
    }
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light mb-4">
      <div className="container-fluid">
        <span className="navbar-brand">ðŸ’¸ Expense Splitter</span>

        <div className="navbar-nav me-auto">
          <button className="btn btn-link nav-link" onClick={() => setView("home")}>
            Home
          </button>
          <button className="btn btn-link nav-link" onClick={() => setView("create")}>
            Create Group
          </button>
          <button className="btn btn-link nav-link" onClick={() => setView("add")}>
            Add Expense
          </button>
        </div>

        <div className="d-flex align-items-center">
          <button className="btn btn-primary me-2" onClick={connect}>
            {account ? `Connected: ${account.slice(0, 6)}...` : "Connect Wallet"}
          </button>
          {account && <span>ðŸ’° {balance} ETH</span>}
        </div>
      </div>
    </nav>
  );
}
