import React, { useEffect, useState } from "react";
import { getContract } from "../utils/contract";
import { parseEther } from "ethers";

export default function SettleUpView({ signer, group, setView }) {
  const [address, setAddress] = useState("");
  const [balances, setBalances] = useState({});
  const [recipient, setRecipient] = useState(null);
  const [amount, setAmount] = useState("");

  useEffect(() => {
    const fetchBalances = async () => {
      const addr = await signer.getAddress();
      setAddress(addr.toLowerCase());

      const res = await fetch(
        `http://localhost:5000/api/expenses/group/${group.groupId}/balance`
      );
      const balanceMap = await res.json();

      const owedTo = group.members.filter((member) => {
        const memberName = member.name;
        const memberAddr = member.address.toLowerCase();
        const balance = balanceMap[memberName];

        return (
          memberAddr !== addr.toLowerCase() &&
          balance > 0 // You owe them
        );
      });

      const balancesFiltered = {};
      owedTo.forEach((m) => {
        const amt = balanceMap[m.name];
        balancesFiltered[m.name] = {
          address: m.address,
          amount: parseFloat(amt).toFixed(4),
        };
      });

      setBalances(balancesFiltered);

      if (Object.keys(balancesFiltered).length > 0) {
        const firstKey = Object.keys(balancesFiltered)[0];
        setRecipient(firstKey);
        setAmount(balancesFiltered[firstKey].amount);
      }
    };

    fetchBalances();
  }, [group, signer]);

  const handleSettle = async () => {
    try {
      const recipientAddress = balances[recipient].address;
      const ethValue = parseEther(amount.toString());

      const contract = getContract(signer);
      const tx = await contract.settleUp(recipientAddress, {
        value: ethValue,
      });

      await tx.wait();
      alert("✅ Settlement successful!");

      setView("groups");
    } catch (err) {
      console.error("Settle failed:", err);
      alert("❌ Transaction failed.");
    }
  };

  return (
    <div className="container mt-5">
      <h3>💸 Settle Up – {group.groupName}</h3>

      {Object.keys(balances).length === 0 ? (
        <p className="text-muted mt-4">You don’t owe anyone in this group.</p>
      ) : (
        <div className="mt-4">
          <div className="mb-3">
            <label className="form-label">Select Member</label>
            <select
              className="form-select"
              value={recipient}
              onChange={(e) => {
                setRecipient(e.target.value);
                setAmount(balances[e.target.value].amount);
              }}
            >
              {Object.entries(balances).map(([name, info]) => (
                <option key={name} value={name}>
                  {name} – {info.amount} ETH
                </option>
              ))}
            </select>
          </div>

          <div className="mb-3">
            <label className="form-label">Amount (ETH)</label>
            <input
              type="text"
              className="form-control"
              value={amount}
              disabled
            />
          </div>

          <button className="btn btn-success me-2" onClick={handleSettle}>
            Confirm Payment
          </button>
          <button className="btn btn-outline-secondary" onClick={() => setView("groups")}>
            Cancel
          </button>
        </div>
      )}
    </div>
  );
}
