import { useState, useEffect } from "react";
import { BrowserProvider, formatEther } from "ethers";

export default function Navbar({ setView, setSigner }) {
  const [account, setAccount] = useState(null);
  const [balance, setBalance] = useState("0");

  const connect = async () => {
    if (window.ethereum) {
      const provider = new BrowserProvider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = await provider.getSigner();
      setSigner(signer);

      const addr = await signer.getAddress();
      setAccount(addr);

      const bal = await provider.getBalance(addr);
      setBalance(formatEther(bal));
    } else {
      alert("Please install MetaMask");
    }
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark shadow-sm sticky-top mb-4 w-100">
    <div className="d-flex justify-content-between align-items-center w-100 px-4">
      <span className="navbar-brand fw-bold text-success">ðŸ’¸ Expense Splitter</span>
  
      <div className="d-flex gap-3">
        <button className="btn btn-outline-light btn-sm" onClick={() => setView("home")}>
          Home
        </button>
        <button className="btn btn-outline-light btn-sm" onClick={() => setView("create")}>
          Create Group
        </button>
        <button className="btn btn-outline-light btn-sm" onClick={() => setView("add")}>
          Add Expense
        </button>
      </div>
  
      <div className="d-flex align-items-center gap-2">
        {account && <span className="text-light">ðŸ’° {parseFloat(balance).toFixed(4)} ETH</span>}
        <button className="btn btn-success btn-sm" onClick={connect}>
          {account ? `Connected: ${account.slice(0, 6)}...` : "Connect Wallet"}
        </button>
      </div>
    </div>
  </nav>  
  );
}
