import React from "react";
import ConnectWallet from "../components/ConnectWallet";
import "../App.css"; // or your specific styling file

const Home = ({ onConnect }) => {
  const handleConnect = async () => {
    if (window.ethereum) {
      try {
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });

        if (accounts.length > 0) {
          const signer = accounts[0]; // basic signer (could later be Ethers signer)
          onConnect(signer); // Send back to App.js
        }
      } catch (err) {
        console.error("Wallet connection failed:", err);
        alert("Wallet connection failed. Make sure MetaMask is installed.");
      }
    } else {
      alert("MetaMask not detected. Please install MetaMask and try again.");
    }
  };

  return (
    <div className="home-container">
      <h1 className="mt-5">ðŸ’¸ Expense Splitter DApp</h1>
      <p className="lead mb-4">Fairly split group expenses with full transparency using Web3.</p>

      <button className="btn btn-success btn-lg" onClick={handleConnect}>
        Connect MetaMask Wallet
      </button>

      <div className="features mt-5">
        <h4>Features:</h4>
        <ul className="list-unstyled">
          <li>âœ… Create groups & add members</li>
          <li>âœ… Record expenses (equal or custom split)</li>
          <li>âœ… View real-time balances & history</li>
          <li>âœ… Backed by Ethereum Smart Contracts</li>
        </ul>
      </div>
    </div>
  );
};

export default Home;
