      <p className="lead">Connected Wallet:</p>
      <h5 className="text-muted">{address}</h5>

      <BalanceDisplay signer={signer} />

      <p className="mt-4">Use the navigation bar to create groups and manage expenses.</p>
    </div>
  );
}
