import React, { useEffect, useState } from "react";
import { getContract } from "../utils/contract";
import { parseEther } from "ethers";

export default function GroupsPage({ signer }) {
  const [address, setAddress] = useState("");
  const [groups, setGroups] = useState([]);
  const [balances, setBalances] = useState({}); // groupId ‚Üí balances map
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchGroupsAndBalances = async () => {
      try {
        const addr = await signer.getAddress();
        setAddress(addr);

        const res = await fetch("http://localhost:5000/api/groups");
        const allGroups = await res.json();
        console.log("üì¶ Fetched groups:", allGroups);
        const myGroups = allGroups.filter(group =>
          group.members.some(m =>
            m.address.toLowerCase() === addr.toLowerCase()
          )
        );

        const balanceData = {};

        for (let group of myGroups) {
          const balanceRes = await fetch(`http://localhost:5000/api/expenses/group/${group.groupId}/balance`);
          const groupBalances = await balanceRes.json();
          balanceData[group.groupId] = groupBalances;
        }

        setGroups(myGroups);
        setBalances(balanceData);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching group balances:", err);
        setLoading(false);
      }
    };

    if (signer) fetchGroupsAndBalances();
  }, [signer]);

  const handleSettleUp = async (recipient, amount) => {
    try {
      const contract = getContract(signer);
      const tx = await contract.settleUp(recipient, {
        value: parseEther(amount.toString())
      });

      console.log("TX sent:", tx.hash);
      await tx.wait();
      alert("‚úÖ Settle up successful!");
    } catch (err) {
      console.error("‚ùå Settle up failed:", err);
      alert("Settle up failed. Check console for details.");
    }
  };

  if (loading) return <div className="text-center mt-4">Loading groups...</div>;

  return (
    <div className="container mt-5">
      <h3 className="mb-4">üìã Your Groups</h3>

      {groups.length === 0 ? (
        <p>You are not in any groups yet.</p>
      ) : (
        groups.map(group => {
          const groupBalances = balances[group.groupId] || {};

          return (
            <div key={group.groupId} className="card mb-4">
              <div className="card-header">
                <h5 className="mb-0">{group.groupName}</h5>
              </div>
              <div className="card-body">
                {Object.entries(groupBalances).map(([name, amount]) => {
                  const isSelf = group.members.find(m => m.name === name)?.address === address;
                  const formattedAmt = Math.abs(amount).toFixed(4);

                  return (
                    <div key={name} className="d-flex justify-content-between align-items-center mb-2">
                      <span>
                        {name} {amount < 0 ? "you owe" : "owes you"} {formattedAmt} ETH
                      </span>
                      {amount < 0 && !isSelf && (
                        <button
                          className="btn btn-sm btn-outline-success"
                          onClick={() => {
                            const recipient = group.members.find(m => m.name === name)?.address;
                            handleSettleUp(recipient, formattedAmt);
                          }}
                        >
                          Settle Up
                        </button>
                      )}
                    </div>
                  );
                })}
              </div>
            </div>
          );
        })
      )}
    </div>
  );
}
