import React, { useEffect, useState } from "react";
import { getContract } from "../utils/contract";
import { parseEther } from "ethers";

export default function GroupsPage({ signer }) {
  const [address, setAddress] = useState("");
  const [groups, setGroups] = useState([]);
  const [balances, setBalances] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchGroupsAndBalances = async () => {
      try {
        const addr = await signer.getAddress();
        setAddress(addr);

        const res = await fetch("http://localhost:5000/api/groups");
        const allGroups = await res.json();

        const myGroups = allGroups.filter(group =>
          group.members.some(m =>
            m.address.toLowerCase() === addr.toLowerCase()
          )
        );

        const balanceMap = {};
        for (let group of myGroups) {
          try {
            const balRes = await fetch(`http://localhost:5000/api/expenses/group/${group.groupId}/balance`);
            const groupBalances = await balRes.json();
            balanceMap[group.groupId] = groupBalances;
          } catch (err) {
            console.warn(`Failed to fetch balances for ${group.groupName}:`, err);
          }
        }

        setGroups(myGroups);
        setBalances(balanceMap);
      } catch (err) {
        console.error("Group fetch error:", err);
      } finally {
        setLoading(false);
      }
    };

    if (signer) fetchGroupsAndBalances();
  }, [signer]);

  const handleSettleUp = (group) => {
    // TODO: Navigate to SettleUp view with selected group
    console.log("Open settle up for group:", group.groupId);
  };

  const openActivity = (groupId) => {
    // TODO: Open modal or redirect to ActivityView
    console.log("Open activity for group:", groupId);
  };

  if (loading) return <div className="text-center mt-4">Loading your groups...</div>;

  return (
    <div className="container mt-5">
      <h3 className="mb-4">ðŸ“‹ Your Groups</h3>

      {groups.length === 0 ? (
        <p>You are not part of any groups yet.</p>
      ) : (
        groups.map(group => {
          const groupBalances = balances[group.groupId] || {};
          const members = group.members;
          const memberMap = {};
          members.forEach(m => {
            memberMap[m.name] = m.address.toLowerCase();
          });

          // Your net balance
          const yourEntry = Object.entries(groupBalances).find(([name]) =>
            memberMap[name] === address.toLowerCase()
          );
          const yourAmount = yourEntry ? parseFloat(yourEntry[1]) : 0;
          const yourText =
            yourAmount > 0
              ? `You are owed ${yourAmount.toFixed(4)} ETH`
              : yourAmount < 0
              ? `You owe ${Math.abs(yourAmount).toFixed(4)} ETH`
              : `You're all settled in this group`;

          return (
            <div key={group.groupId} className="card mb-4 shadow-sm">
              <div className="card-header d-flex justify-content-between align-items-center">
                <h5 className="mb-0">{group.groupName}</h5>
                <div>
                  <button
                    className="btn btn-sm btn-outline-secondary me-2"
                    onClick={() => openActivity(group.groupId)}
                  >
                    Activity
                  </button>
                  <button
                    className="btn btn-sm btn-outline-primary"
                    onClick={() => handleSettleUp(group)}
                  >
                    Settle Up
                  </button>
                </div>
              </div>
              <div className="card-body">
                <p><strong>{yourText}</strong></p>
                <hr />
                {Object.entries(groupBalances).map(([name, amount]) => {
                  const lower = memberMap[name];
                  if (lower === address.toLowerCase()) return null;

                  const formatted = Math.abs(amount).toFixed(4);
                  const isOwed = amount > 0;

                  return (
                    <div key={name} className="d-flex justify-content-between align-items-center mb-2">
                      <span>
                        {name}{" "}
                        <span style={{ color: isOwed ? "green" : "red", fontWeight: "bold" }}>
                          {isOwed ? "is owed" : "owes"} {formatted} ETH
                        </span>
                      </span>
                    </div>
                  );
                })}
              </div>
            </div>
          );
        })
      )}
    </div>
  );
}
