import { useState } from "react";

export default function CreateGroup({ setGroupId }) {
  const [groupName, setGroupName] = useState("");
  const [members, setMembers] = useState([""]);

  const handleCreate = async () => {
    const res = await fetch("http://localhost:3000/api/groups", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        groupName,
        createdBy: members[0],
        members,
      }),
    });
    const data = await res.json();
    if (data.groupId) {
      alert("Group created: " + data.groupId);
      setGroupId(data.groupId);
    }
  };

  return (
    <div>
      <h3>Create Group</h3>
      <input className="form-control mb-2" placeholder="Group Name" value={groupName} onChange={(e) => setGroupName(e.target.value)} />
      {members.map((m, i) => (
        <input key={i} className="form-control mb-2" placeholder="Member Address" value={m} onChange={(e) => {
          const newMembers = [...members];
          newMembers[i] = e.target.value;
          setMembers(newMembers);
        }} />
      ))}
      <button className="btn btn-secondary me-2" onClick={() => setMembers([...members, ""])}>+ Add Member</button>
      <button className="btn btn-success" onClick={handleCreate}>Create Group</button>
    </div>
  );
}
