import React, { useEffect, useState } from "react";
import { getContract } from "../utils/contract";
import { parseEther } from "ethers";

export default function GroupsPage({ signer }) {
  const [address, setAddress] = useState("");
  const [groups, setGroups] = useState([]);
  const [balances, setBalances] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchGroupsAndBalances = async () => {
      try {
        const addr = await signer.getAddress();
        setAddress(addr);

        const res = await fetch("http://localhost:5000/api/groups");
        const allGroups = await res.json();

        const myGroups = allGroups.filter(group =>
          group.members.some(m =>
            m.address.toLowerCase() === addr.toLowerCase()
          )
        );

        const balanceMap = {};

        for (let group of myGroups) {
          try {
            const balRes = await fetch(`http://localhost:5000/api/expenses/group/${group.groupId}/balance`);
            const groupBalances = await balRes.json(); // key = name, value = amount
            balanceMap[group.groupId] = groupBalances;
          } catch (err) {
            console.warn(`Failed to fetch balance for group ${group.groupId}`, err);
          }
        }

        setGroups(myGroups);
        setBalances(balanceMap);
      } catch (err) {
        console.error("Failed to fetch groups or balances:", err);
      } finally {
        setLoading(false);
      }
    };

    if (signer) fetchGroupsAndBalances();
  }, [signer]);

  const handleSettleUp = async (recipient, amountEth) => {
    try {
      const contract = getContract(signer);
      const tx = await contract.settleUp(recipient, {
        value: parseEther(amountEth.toString())
      });
      await tx.wait();
      alert("‚úÖ Settle up successful!");
    } catch (err) {
      console.error("Settle up failed:", err);
      alert("‚ùå Transaction failed.");
    }
  };

  if (loading) return <div className="text-center mt-4">Loading groups...</div>;

  return (
    <div className="container mt-5">
      <h3 className="mb-4">üìã Your Groups</h3>

      {groups.length === 0 ? (
        <p>You are not part of any groups yet.</p>
      ) : (
        groups.map(group => {
          const groupBalances = balances[group.groupId] || {};

          return (
            <div key={group.groupId} className="card mb-4 shadow-sm">
              <div className="card-header">
                <h5 className="mb-0">{group.groupName}</h5>
              </div>
              <div className="card-body">
                {Object.entries(groupBalances).length === 0 ? (
                  <p className="text-muted">No expenses in this group yet.</p>
                ) : (
                  Object.entries(groupBalances).map(([name, amount]) => {
                    const member = group.members.find(m => m.name === name);
                    const memberAddress = member?.address.toLowerCase() || "";
                    const currentUserAddress = address.toLowerCase();

                    const isYou = memberAddress === currentUserAddress;
                    const shouldShowSettle = !isYou && parseFloat(amount) > 0;

                    const absAmount = Math.abs(amount).toFixed(4);

                    return (
                      <div key={name} className="d-flex justify-content-between align-items-center mb-2">
                        <span>
                          {isYou ? "You" : name} {amount > 0 ? "are owed" : "owes you"} {absAmount} ETH
                        </span>
                        {shouldShowSettle && (
                          <button
                            className="btn btn-sm btn-outline-success"
                            onClick={() => handleSettleUp(memberAddress, absAmount)}
                          >
                            Settle Up
                          </button>
                        )}
                      </div>
                    );
                  })
                )}
              </div>
            </div>
          );
        })
      )}
    </div>
  );
}
