import { useEffect, useState } from "react";
import { BrowserProvider } from "ethers";

export default function AddExpense() {
  const [allGroups, setAllGroups] = useState([]);
  const [selectedGroupId, setSelectedGroupId] = useState("");
  const [group, setGroup] = useState(null);

  const [paidBy, setPaidBy] = useState("");
  const [description, setDescription] = useState("");
  const [amount, setAmount] = useState("");
  const [splitType, setSplitType] = useState("equal"); // or "manual"
  const [splitDetails, setSplitDetails] = useState({});

  // Load all groups for the dropdown
  useEffect(() => {
    const loadGroups = async () => {
      const res = await fetch("http://localhost:5000/api/groups");
      const data = await res.json();
      setAllGroups(data);
    };
    loadGroups();
  }, []);

  // Load group details when selected
  useEffect(() => {
    const selected = allGroups.find((g) => g.groupId === selectedGroupId);
    if (selected) {
      setGroup(selected);

      // Initialize split fields for members
      const initialSplit = {};
      selected.members.forEach((m) => {
        initialSplit[m.address] = "";
      });
      setSplitDetails(initialSplit);
    }
  }, [selectedGroupId, allGroups]);

  // Auto-fill "Paid By" address from connected wallet
  useEffect(() => {
    const fetchConnectedWallet = async () => {
      try {
        const provider = new BrowserProvider(window.ethereum);
        const signer = await provider.getSigner();
        const addr = await signer.getAddress();

        if (group) {
          const match = group.members.find((m) => m.address.toLowerCase() === addr.toLowerCase());
          if (match) {
            setPaidBy(match.address); // or match.name if you'd prefer showing name
            return;
          }
        }

        setPaidBy(addr);
      } catch (err) {
        console.error("Error auto-filling wallet address:", err);
      }
    };

    if (group) fetchConnectedWallet();
  }, [group]);

  const calculateEqualSplit = () => {
    if (!group || !amount) return {};
    const perPerson = parseFloat(amount) / group.members.length;
    const result = {};
    group.members.forEach((m) => {
      result[m.address] = perPerson.toFixed(4);
    });
    return result;
  };

  const handleSplitChange = (address, value) => {
    setSplitDetails((prev) => ({
      ...prev,
      [address]: value
    }));
  };

  const handleSubmit = async () => {
    const splitBetween =
      splitType === "equal"
        ? Object.keys(calculateEqualSplit())
        : Object.keys(splitDetails).filter((addr) => parseFloat(splitDetails[addr]) > 0);

    const finalSplit =
      splitType === "equal" ? calculateEqualSplit() : splitDetails;

    const payload = {
      groupId: selectedGroupId,
      paidBy,
      amount: parseFloat(amount),
      description,
      splitBetween,
      splitDetails: finalSplit
    };

    const res = await fetch("http://localhost:5000/api/expenses", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload)
    });

    const data = await res.json();
    if (data.expenseId) {
      alert("✅ Expense added!");
      // Optionally reset form or redirect
    } else {
      alert("❌ Failed to add expense");
    }
  };

  return (
    <div className="container mt-4">
      <h3>Add New Expense</h3>

      {/* Group Selection */}
      <div className="mb-3">
        <label>Select Group:</label>
        <select
          className="form-select"
          value={selectedGroupId}
          onChange={(e) => setSelectedGroupId(e.target.value)}
        >
          <option value="">-- Select a Group --</option>
          {allGroups.map((g) => (
            <option key={g.groupId} value={g.groupId}>
              {g.groupName}
            </option>
          ))}
        </select>
      </div>

      {group && (
        <>
          <div className="mb-3">
            <label>Paid By (Your Wallet Address):</label>
            <input
              className="form-control"
              value={paidBy}
              onChange={(e) => setPaidBy(e.target.value)}
            />
          </div>

          <div className="mb-3">
            <label>Description:</label>
            <input
              className="form-control"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </div>

          <div className="mb-3">
            <label>Amount in ETH:</label>
            <input
              className="form-control"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
            />
          </div>

          <div className="mb-3">
            <label>Split Type:</label>
            <select
              className="form-select"
              value={splitType}
              onChange={(e) => setSplitType(e.target.value)}
            >
              <option value="equal">Equal Split</option>
              <option value="manual">Manual Split</option>
            </select>
          </div>

          <div className="mb-4">
            <label>Split Between:</label>
            {group.members.map((m, idx) => (
              <div key={idx} className="mb-2">
                <strong>{m.name}</strong> ({m.address.slice(0, 6)}...{m.address.slice(-4)})
                <input
                  type="text"
                  className="form-control mt-1"
                  disabled={splitType === "equal"}
                  value={
                    splitType === "equal"
                      ? calculateEqualSplit()[m.address]
                      : splitDetails[m.address]
                  }
                  onChange={(e) => handleSplitChange(m.address, e.target.value)}
                />
              </div>
            ))}
          </div>

          <button className="btn btn-primary" onClick={handleSubmit}>
            Submit Expense
          </button>
        </>
      )}
    </div>
  );
}
