import React, { useEffect, useState } from "react";
import { getContract } from "../utils/contract";
import { parseEther } from "ethers";

export default function GroupsPage({ signer }) {
  const [address, setAddress] = useState("");
  const [groups, setGroups] = useState([]);
  const [balances, setBalances] = useState({}); // groupId => balances
  const [loading, setLoading] = useState(true);

  // Fetch groups and balances
  useEffect(() => {
    const fetchGroupsAndBalances = async () => {
      try {
        const addr = await signer.getAddress();
        setAddress(addr);

        const res = await fetch("http://localhost:5000/api/groups");
        const allGroups = await res.json();

        if (!Array.isArray(allGroups)) {
          console.error("‚ùå Invalid groups response:", allGroups);
          setLoading(false);
          return;
        }

        const myGroups = allGroups.filter(group =>
          group.members.some(m =>
            m.address.toLowerCase() === addr.toLowerCase()
          )
        );

        const balanceMap = {};

        for (let group of myGroups) {
          try {
            const balanceRes = await fetch(`http://localhost:5000/api/expenses/group/${group.groupId}/balance`);
            const groupBalances = await balanceRes.json();
            balanceMap[group.groupId] = groupBalances;
            console.log(`üìä Balances for ${group.groupName}:`, groupBalances);
          } catch (err) {
            console.warn(`‚ö†Ô∏è Failed to fetch balance for group ${group.groupId}`, err);
          }
        }

        setGroups(myGroups);
        setBalances(balanceMap);
      } catch (err) {
        console.error("‚ùå Error fetching groups or balances:", err);
      } finally {
        setLoading(false);
      }
    };

    if (signer) fetchGroupsAndBalances();
  }, [signer]);

  const handleSettleUp = async (recipient, amountEth) => {
    try {
      const contract = getContract(signer);
      const tx = await contract.settleUp(recipient, {
        value: parseEther(amountEth.toString())
      });

      console.log("üì§ SettleUp TX sent:", tx.hash);
      await tx.wait();
      alert("‚úÖ Settled successfully!");
    } catch (err) {
      console.error("‚ùå Settle Up failed:", err);
      alert("Settle up failed.");
    }
  };

  if (loading) return <div className="text-center mt-4">Loading your groups...</div>;

  return (
    <div className="container mt-5">
      <h3 className="mb-4">üìã Your Groups</h3>

      {groups.length === 0 ? (
        <p>You are not part of any groups yet.</p>
      ) : (
        groups.map(group => {
          const groupBalances = balances[group.groupId] || {};
          const membersMap = Object.fromEntries(group.members.map(m => [m.name, m.address]));

          return (
            <div key={group.groupId} className="card mb-4 shadow-sm">
              <div className="card-header">
                <h5 className="mb-0">{group.groupName}</h5>
              </div>
              <div className="card-body">
                {Object.entries(groupBalances).length === 0 ? (
                  <p className="text-muted">No expenses in this group yet.</p>
                ) : (
                  Object.entries(groupBalances).map(([name, amount]) => {
                    const isYou = membersMap[name]?.toLowerCase() === address.toLowerCase();
                    const showSettle = amount < 0 && !isYou;
                    const absAmount = Math.abs(amount).toFixed(4);
                    const recipient = membersMap[name];

                    return (
                      <div key={name} className="d-flex justify-content-between align-items-center mb-2">
                        <span>
                          {isYou ? "You" : name} {amount > 0 ? "are owed" : "owe"} {absAmount} ETH
                        </span>
                        {showSettle && (
                          <button
                            className="btn btn-sm btn-outline-success"
                            onClick={() => handleSettleUp(recipient, absAmount)}
                          >
                            Settle Up
                          </button>
                        )}
                      </div>
                    );
                  })
                )}
              </div>
            </div>
          );
        })
      )}
    </div>
  );
}
