import { useState } from "react";
import Navbar from "./components/Navbar";
import CreateGroup from "./views/CreateGroup";
import AddExpense from "./views/AddExpense";
import GroupSummary from "./views/GroupSummary";
import GroupSelector from "./components/GroupSelector";
import Home from "./views/Home"; // <-- Add Home page import

function App() {
  const [signer, setSigner] = useState(null);
  const [view, setView] = useState("home");
  const [selectedGroupId, setSelectedGroupId] = useState(null);

  const handleWalletConnect = (connectedSigner) => {
    setSigner(connectedSigner);
    setView("dashboard"); // Redirect to dashboard after login
  };

  return (
    <div className="App">
      <Navbar setView={setView} setSigner={setSigner} />

      {/* Home Page Before Wallet Connect */}
      {!signer && view === "home" && (
        <Home onConnect={handleWalletConnect} />
      )}

      {/* Dashboard (After wallet connect) */}
      {signer && view === "dashboard" && (
        <>
          <GroupSelector onSelect={setSelectedGroupId} />
          {selectedGroupId ? (
            <GroupSummary groupId={selectedGroupId} />
          ) : (
            <p className="text-center mt-5">
              Please select a group to get started.
            </p>
          )}
        </>
      )}

      {/* Create Group */}
      {signer && view === "create" && (
        <CreateGroup setGroupId={setSelectedGroupId} />
      )}

      {/* Add Expense */}
      {signer && view === "add" && selectedGroupId && (
        <AddExpense groupId={selectedGroupId} />
      )}
    </div>
  );
}

export default App;
