import { useState } from "react";
import { getContract } from "../utils/contract";
import { parseEther } from "ethers";

export default function ContractActions({ signer }) {
    const [depositAmount, setDepositAmount] = useState("");
    const [recipients, setRecipients] = useState("");
    const [amounts, setAmounts] = useState("");

    const deposit = async () => {
        try {
            if (!depositAmount || isNaN(depositAmount)) {
                alert("Please enter a valid amount in ETH.");
                return;
            }
    
            const contract = getContract(signer);
            const tx = await contract.deposit({
                value: parseEther(depositAmount)
            });
            await tx.wait();
            alert("âœ… Deposit successful!");
        } catch (error) {
            console.error("âŒ Deposit failed:", error);
    
            // Show cleaner error message to the user
            if (error.code === "INSUFFICIENT_FUNDS") {
                alert("âŒ Not enough ETH in your wallet for this transaction.");
            } else if (error.message.includes("value must be")) {
                alert("âŒ Invalid ETH value entered.");
            } else {
                alert("âŒ Transaction failed: " + (error?.message || "Unknown error"));
            }
        }
    };
    

    const splitExpense = async () => {
        const contract = getContract(signer);
        const addressList = recipients.split(",").map(addr => addr.trim());
        const amountList = amounts.split(",").map(val => parseEther(val.trim()));

        const tx = await contract.splitExpense(addressList, amountList);
        await tx.wait();
        alert("Expense split successfully!");
    };

    return (
        <div className="container mt-4">
            <h3>ðŸ’¸ Deposit ETH</h3>
            <input
                className="form-control mb-2"
                placeholder="Amount in ETH"
                value={depositAmount}
                onChange={e => setDepositAmount(e.target.value)}
            />
            <button className="btn btn-success mb-4" onClick={deposit}>Deposit</button>

            <h3>ðŸ“¤ Split Expense</h3>
            <input
                className="form-control mb-2"
                placeholder="Comma-separated addresses"
                value={recipients}
                onChange={e => setRecipients(e.target.value)}
            />
            <input
                className="form-control mb-2"
                placeholder="Comma-separated amounts in ETH"
                value={amounts}
                onChange={e => setAmounts(e.target.value)}
            />
            <button className="btn btn-warning" onClick={splitExpense}>Split</button>
        </div>
    );
}
