import React, { useEffect, useState } from "react";
import { formatEther, BrowserProvider } from "ethers";

export default function WalletDashboard({ signer }) {
  const [address, setAddress] = useState("");
  const [walletBalance, setWalletBalance] = useState("0");

  useEffect(() => {
    const fetchWalletInfo = async () => {
      if (signer) {
        const addr = await signer.getAddress();
        setAddress(addr);

        const provider = new BrowserProvider(window.ethereum);
        const balance = await provider.getBalance(addr);
        setWalletBalance(formatEther(balance));
      }
    };

    fetchWalletInfo();
  }, [signer]);

  return (
    <div className="container mt-5 text-center">
      <h2>ðŸŽ‰ Welcome to Your Dashboard</h2>
      <p className="lead">Connected Wallet:</p>
      <h5 className="text-muted">{address}</h5>

      <h4 className="mt-4">ðŸ’° Wallet Balance: {walletBalance} ETH</h4>

      <p className="mt-4">Use the navigation bar to create groups and manage expenses.</p>
    </div>
  );
}
