import { useState, useEffect } from "react";
import { BrowserProvider, formatEther } from "ethers";

export default function Navbar({ setView, setSigner, signer, view }) {
  const [account, setAccount] = useState(null);
  const [balance, setBalance] = useState("0");

  const connect = async () => {
    if (window.ethereum) {
      const provider = new BrowserProvider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = await provider.getSigner();
      setSigner(signer);

      const addr = await signer.getAddress();
      setAccount(addr);

      const bal = await provider.getBalance(addr);
      setBalance(formatEther(bal));
      setView("dashboard");
    } else {
      alert("Please install MetaMask");
    }
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark navbar-custom py-3 px-4 shadow-sm sticky-top">
      <div className="container-fluid">
        <span className="navbar-brand fw-bold text-info">
          <i className="fas fa-coins me-2"></i>Expense Splitter
        </span>

        <div className="d-flex ms-auto gap-3 align-items-center">
          <button className="btn btn-outline-light btn-sm" onClick={() => setView("home")}>
            Home
          </button>
          <button className="btn btn-outline-success btn-sm" onClick={() => setView("create")}>
            Create Group
          </button>
          <button className="btn btn-outline-primary btn-sm" onClick={() => setView("add")}>
            Add Expense
          </button>

          {account && (
            <>
              <span className="text-light small">ðŸ’° {parseFloat(balance).toFixed(4)} ETH</span>
              <span className="text-light small">
                {`ðŸ”— ${account.slice(0, 6)}...${account.slice(-4)}`}
              </span>
            </>
          )}

          {/* Show connect button only if not on homepage or already connected */}
          {!(view === "home" && !signer) && (
            <button className="btn btn-success btn-sm" onClick={connect}>
              {account ? "Connected" : "Connect Wallet"}
            </button>
          )}
        </div>
      </div>
    </nav>
  );
}
